<script lang="ts">
	import { browser } from '$app/env';
	import { createPopperActions } from 'svelte-popperjs';
	import { derivedPings } from '../store/pings';
	import { fade } from 'svelte/transition';

	const [popperRef, popperContent] = createPopperActions();
	const popperOptions = {
		placement: 'top',
		modifiers: [{ name: 'offset', options: { offset: [0, 8] } }]
	};

	let svgElement: SVGElement;
	let nodes: NodeListOf<SVGElement>;
	let ping: number = NaN;
	let popper = {
		isOpen: false,
		city: null
	};

	async function onOpen(node: SVGElement | HTMLElement) {
		popper = {
			isOpen: true,
			city: node.classList[1]
		};
		popperRef(node as any);
	}
	function onClose() {
		popper = {
			isOpen: false,
			city: null
		};
	}

	$: {
		if (svgElement) {
			nodes = document.querySelectorAll('.hub');
			nodes.forEach((node) => {
				node.addEventListener('mouseenter', () => onOpen(node));
				node.addEventListener('mouseleave', onClose);
			});
		}
	}

	$: {
		try {
			ping = $derivedPings[popper.city];
		} catch {}
	}
</script>

<div class="absolute top-0 bottom-0 left-0 right-0 pointer-events-none z-10">
	{#if popper.isOpen}
		<div
			id="tooltip"
			role="tooltip"
			transition:fade
			class="px-4 py-1 rounded-sm bg-pink-600 font-bold"
			use:popperContent={popperOptions}
		>
			{popper.city}
			{ping} ms
			<div id="arrow" data-popper-arrow />
		</div>
	{/if}
</div>

